import './App.css';
import { useState, useEffect } from 'react'; //creating state - useState hook

function App() {
  const [listOfUsers, setListOfUsers] = useState([]); //[creating a state that is a list, function used to alter state]

  useEffect(() => {
    Axios.get("http://localhost:3000/getUsers").then((respons) => {
      setListOfUSers(response.data);
    })
  }, [])

  return (
    <div className="App">
      <div className='usersDisplay'>
        {listOfUsers.map((user) => {
          return (
            <div> 
              <h1>Name: {user.name}</h1>
              <h1>Age: {user.age}</h1>
              <h1>Username: {user.username}</h1>
           </div>
        );
      })}
      </div>
    </div>
  );
}

export default App;
